import React from 'react';
import {Link} from "react-router-dom";
import data from "../assets/data/beds";

const ProductPage = (data, id) => {
    return (
        <div>
            <button className="productPage__goBackButton" type={"button"}><Link to={`/furniture/${props.data[id].category}`}>Go back</Link></button>
            <div className="productPage__content">
                <div className="productPage__img"><img width={300} src={props.data[id].img} alt=""/></div>
                <div className="productPage__info">
                    <h2 className="productPage__title">{props.data[id].title}</h2>
                    <h3 className="productPage__category">{props.data[id].category}</h3>
                    <p className="productPage__description">{props.data[id].description}</p>
                    <img width={150} src={props.data[id].rating} alt=""/>
                    <h2 className="productPage__price">${props.data[id].price}</h2>
                    <button className="productPage__button" type={"button"}>Add to cart</button>
                </div>
            </div>
        </div>
    );
};

export default ProductPage;
