import React, {useRef} from 'react';
import SliderItem from "./SliderItem";
import sofa from  "../assets/images/sofa.png"
import rating from  "../assets/images/rating.png"
import Slider from "react-slick";
import prev from "../assets/images/prev.png";
import next from "../assets/images/next.png";
const SofaSlider = () => {
    const sofas = [
        {id:0, category: "Sofa", img: sofa, title:"Sakarias Sofa", rating: rating, price: 392*1.5},
        {id:1, category: "Sofa", img: sofa, title:"Baltsar Sofa", rating: rating, price: 299*1.5},
        {id:2, category: "Sofa", img: sofa, title:"Anjay Sofa", rating: rating, price: 519*1.5},
        {id:3, category: "Sofa", img: sofa, title:"Nyantuy Sofa", rating: rating, price: 921*1.5},
        {id:4, category: "Sofa", img: sofa, title:"Mila Sofa", rating: rating, price: 999*1.6},
        {id:5, category: "Sofa", img: sofa, title:"Unvel Sofa", rating: rating, price: 998*1.7},

    ]

    const settings = {
        dots: false,
        arrows: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1
    }

    const slider = useRef(null)

    return (
        <div className="product__category">
            <Slider ref={slider} {...settings}>
                {sofas.map((item) => <SliderItem item = {item} />)
                }
            </Slider>
            <button onClick={() => slider?.current?.slickPrev()}><img className="slider__prevButton" src={prev} alt=""/></button>
            <button onClick={() => slider?.current?.slickNext()}><img className="slider__nextButton" src={next} alt=""/></button>
        </div>
    );
};

export default SofaSlider;
