import React, {useEffect, useRef, useState} from 'react';
import cartImg from "../assets/images/cart.png"
import {Link} from "react-router-dom";
import cart from "../assets/data/cart";

const NavBar = () => {

    const [myArray, setMyArray] = useState(cart);
    const [myArrayLength, setMyArrayLength] = useState(0);

    useEffect(() => {
        setMyArray(cart)
        setMyArrayLength(myArray.length);
        console.log("render")
    }, [myArray, cart, cart.length, myArrayLength]);

    return (
        <div className={"navbar"}>
            <Link to="/" ><div className="navbar__logo">Panto</div></Link>
            <div className="navbar__nav">
                <div className="navbar__nav__item"><Link to="/">Home</Link></div>
                <div className="navbar__nav__item"><Link to="/furniture">Furniture</Link></div>
                <div className="navbar__nav__item"><Link to="/about" >About us</Link></div>
                <div className="navbar__nav__item"> <Link to="/contacts">Contacts </Link></div>
            </div>
            <div className="navbar__cart">
                <img src={cartImg} alt=""/>
                <div className="navbar__cart__content">{myArrayLength}</div>
            </div>
        </div>
    );
};

export default NavBar;
