import React, {useEffect, useReducer, useState} from 'react';
import cart from "../assets/data/cart";
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
const CartItem = (props) => {
    const [reducerValue, forceUpdate] = useReducer(x => x + 1, 0);

    const [inputCount, setInputCount] = useState(props.item.count + 1 - 1)


    const confirmDelete = () => {
        confirmAlert({
            title: 'Удаление товара',
            message: 'Вы хотите удалить этот товар из вашей корзины?',
            buttons: [
                {
                    label: 'Да',
                    onClick: () => {
                        props.item.count = 0
                        cart.pop(props.item)
                        forceUpdate()
                    }
                },
                {
                    label: 'Нет',
                }
            ],
            closeOnEscape: true,
            closeOnClickOutside: true,
        });
    };
    const countLeftHandler = () => {
        if (inputCount !== 1) {
            setInputCount(inputCount - 1)
        }
        else {
            confirmDelete()
            if (confirmDelete) {
                props.item.count = 0
                cart.pop(props.item)
            }
        }

       forceUpdate()
    }
    const countRightHandler = () => {
        if (inputCount !== 99) {
            setInputCount(inputCount + 1)
        }
        forceUpdate()
    }

    return (
        <div className="container">
            <div className="cartItem__content">
                <img className="cartItem__img" src={props.item.img} alt=""/>
                <div className="cartItem__info">
                    <h1 className="cartItem__title">{props.item.title}</h1>
                    <img src={props.item.rating} alt="" className="cartItem__rating"/>
                    <p className="cartItem__description">{props.item.description}</p>
                    <p className="cartItem__price">${props.item.price * inputCount}</p>
                    <div className="cartItem__purchaseSection">
                        <button className="cartItem__count countLeft" onClick={countLeftHandler} >{"<"}</button>
                        <input type="text" className="cartItem__counter" value={inputCount}/>
                        <button className="cartItem__count countRight" onClick={countRightHandler}>{">"}</button>
                        <button className="cartItem__purchaseButton">Buy</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
