import React, {useRef} from 'react';
import SliderItem from "./SliderItem";
import bed from  "../assets/images/bed.png"
import rating from  "../assets/images/rating.png"
import Slider from "react-slick";
import prev from "../assets/images/prev.png";
import next from "../assets/images/next.png";
const BedSlider = () => {
    const beds = [
        {id:0, category: "Bed", img: bed, title:"Sakarias Bed", rating: rating, price: 392*2},
        {id:1, category: "Bed", img: bed, title:"Baltsar Bed", rating: rating, price: 299*2},
        {id:2, category: "Bed", img: bed, title:"Anjay Bed", rating: rating, price: 519*2},
        {id:3, category: "Bed", img: bed, title:"Nyantuy Bed", rating: rating, price: 921*2},
        {id:4, category: "Bed", img: bed, title:"Mila Bed", rating: rating, price: 999*2},
        {id:5, category: "Bed", img: bed, title:"Unvel Bed", rating: rating, price: 998*2},

    ]
    const settings = {
        dots: false,
        arrows: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1
    }

    const slider = useRef(null)

    return (
        <div className="product__category">
            <Slider ref={slider} {...settings}>
                {beds.map((item) => <SliderItem item = {item} />)
                }
            </Slider>
            <button onClick={() => slider?.current?.slickPrev()}><img className="slider__prevButton" src={prev} alt=""/></button>
            <button onClick={() => slider?.current?.slickNext()}><img className="slider__nextButton" src={next} alt=""/></button>
        </div>
    );
};

export default BedSlider;
