import React, {useEffect, useState} from 'react';
import NavBar from "../components/NavBar";
import Footer from "../components/Footer";
import {useParams} from "react-router-dom";
import data from "../assets/data/chairs"
import addToCartBtn from "../assets/images/AddToCartBtn.png";

const ProductPage = () => {
    const {id} = useParams()

    return (

        <div className="container">
            <NavBar/>
            <div className="productPage__content">
                <div className="productPage__img"><img width={300} src={data[id].img} alt=""/></div>
                <div className="productPage__info">
                    <h2 className="productPage__title">{data[id].title}</h2>
                    <h3 className="productPage__category">{data[id].category}</h3>
                    <p className="productPage__description">{data[id].description}</p>
                    <img src={data[id].rating} alt=""/>
                    <div className="product__card__purchase">
                        <h3 className="product__card__price"> <span className="product__card__price__dollar">$</span>  {data[id].price}</h3>
                        <img src={addToCartBtn} alt="" className="product__card__addToCartBtn"/>
                    </div>
                </div>
            </div>
            <Footer/>
        </div>
    );
};

export default ProductPage;
