import React, {useEffect, useState} from 'react';



const ProductSlider = () => {

    const tabs = [
        {index: 0},
        {index: 1},
        {index: 2},
        {index: 3},
    ]

    const [activeTab, setActiveTab] = useState(0)
    const [activeContent, setActiveContent] = useState(0)

    const toggleTab = (index) => {
        setActiveTab(index)
    }
    useEffect( ()=> {
        setActiveContent(true)
    }, toggleTab)
    const toggleContent = () => {
        activeContent === true
    }

    return (
        <div className="container">
            <div className="tabs" >
                <div className="tab__panel">
                    <div className="tab__buttonPanel">
                        {tabs.map( tab=> {
                            <button className="tab__button" onClick={() => toggleTab(tab.index)}>Chair</button>
                        })}
                    </div>
                </div>
                <div className="tab">Chairs</div>
                <div className="tab">Beds</div>
                <div className="tab">Sofas</div>
                <div className="tab">Lamps</div>
            </div>
        </div>
    );
};

export default ProductSlider;
