import React, {useEffect, useRef, useState} from 'react';
import SliderItem from "./SliderItem";
import chair0 from  "../assets/images/chair0.png"
import chair1 from  "../assets/images/chair1.png"
import chair2 from  "../assets/images/chair2.png"
import chair3 from  "../assets/images/chair3.png"
import rating from  "../assets/images/rating.png"
import prev from    "../assets/images/prev.png"
import next from    "../assets/images/next.png"
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ChairSlider = () => {
    const chairs = [
        {id:0, category: "Chair", img: chair0, title:"Sakarias Armchair", rating: rating, price: 392},
        {id:1, category: "Chair", img: chair1, title:"Baltsar Chair", rating: rating, price: 299},
        {id:2, category: "Chair", img: chair2, title:"Anjay Chair", rating: rating, price: 519},
        {id:3, category: "Chair", img: chair3, title:"Nyantuy Chair", rating: rating, price: 921},
        {id:4, category: "Chair", img: chair1, title:"Nyantuy1 Chair", rating: rating, price: 921},
        {id:5, category: "Chair", img: chair2, title:"Nyantuy2 Chair", rating: rating, price: 921},

    ]
    const settings = {
        dots: false,
        arrows: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1
    }

    const slider = useRef(null)

    return (
        <div className="product__category">
            <Slider ref={slider} {...settings}>
                {chairs.map((item) => <SliderItem item = {item} />)
            }
            </Slider>
            <button onClick={() => slider?.current?.slickPrev()}><img className="slider__prevButton" src={prev} alt=""/></button>
            <button onClick={() => slider?.current?.slickNext()}><img className="slider__nextButton" src={next} alt=""/></button>
        </div>
    );
};

export default ChairSlider;
