import React, {useEffect, useState} from 'react';



const ProductSlider = () => {

    const tabs = [
        {index: 0},
        {index: 1},
        {index: 2},
        {index: 3},
    ]

    const [activeTab, setActiveTab] = useState(0)
    const [activeContent, setActiveContent] = useState(0)
    const activeButton = "tab__button tab_bu"
    const toggleTab = (index) => {
        setActiveTab(index)
        setActiveContent(index)
    }



    return (
        <div className="container">
            <div className="tabs" >
                <div className="tab__panel">
                    <div className="tab__buttonPanel">
                        <button className="tab__button" onClick={() => toggleTab(0)}>Chair</button>
                        <button className="tab__button" onClick={() => toggleTab(1)}>Beds</button>
                        <button className="tab__button" onClick={() => toggleTab(2)}>Sofa</button>
                        <button className="tab__button" onClick={() => toggleTab(3)}>Lamp</button>
                    </div>
                </div>
                <div className="tab">Chairs</div>
                <div className="tab">Beds</div>
                <div className="tab">Sofas</div>
                <div className="tab">Lamps</div>
            </div>
        </div>
    );
};

export default ProductSlider;
