import React, {useRef} from 'react';
import SliderItem from "./SliderItem";
import lamp from  "../assets/images/lamp.png"
import rating from  "../assets/images/rating.png"
import Slider from "react-slick";
import prev from "../assets/images/prev.png";
import next from "../assets/images/next.png";
const LampSlider = () => {
    const lamps = [
        {id:0, category: "Lamp", img: lamp, title:"Sakarias Lamp", rating: rating, price: 392*0.5},
        {id:1, category: "Lamp", img: lamp, title:"Baltsar Lamp", rating: rating, price: 299*0.5},
        {id:2, category: "Lamp", img: lamp, title:"Anjay Lamp", rating: rating, price: 519*0.5},
        {id:3, category: "Lamp", img: lamp, title:"Nyantuy Lamp", rating: rating, price: 421*0.5},
        {id:4, category: "Lamp", img: lamp, title:"Mila Lamp1", rating: rating, price: 421*0.5},
        {id:5, category: "Lamp", img: lamp, title:"Unvel Lamp", rating: rating, price: 421*0.5},

    ]

    const settings = {
        dots: false,
        arrows: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1
    }

    const slider = useRef(null)

    return (
        <div className="product__category">
            <Slider ref={slider} {...settings}>
                {lamps.map((item) => <SliderItem item = {item} />)
                }
            </Slider>
            <button onClick={() => slider?.current?.slickPrev()}><img className="slider__prevButton" src={prev} alt=""/></button>
            <button onClick={() => slider?.current?.slickNext()}><img className="slider__nextButton" src={next} alt=""/></button>

        </div>
    );
};

export default LampSlider;
