import React, {useRef} from 'react';
import ProductCard from "./ProductCard";
import chairs from "../assets/data/chairs";
import cart from "../assets/data/cart";
import prev from    "../assets/images/prev.png"
import next from    "../assets/images/next.png"
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ChairSlider = () => {
    let cartArray = cart

    const settings = {
        dots: false,
        arrows: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1
    }

    const addToCart = (item) => {
        cartArray.push(item)
    }

    const slider = useRef(null)

    return (
        <div className="product__category">
            <Slider ref={slider} {...settings}>
                {chairs.map((item) => <ProductCard key={item.id} item = {item} addToCart={addToCart()} />)
            }
            </Slider>
            <button onClick={() => slider?.current?.slickPrev()}><img className="slider__prevButton" src={prev} alt=""/></button>
            <button onClick={() => slider?.current?.slickNext()}><img className="slider__nextButton" src={next} alt=""/></button>
        </div>
    );
};

export default ChairSlider;
